// ======================================== //
// Data types.
// ======================================== //

interface T_Worker
{
	id:    string;	// Worker id.
	name:  string;	// Worker name.
	color: string;	// Color code.
	order: number;	// Worker order.
}

interface T_WorkerTask
{
	id:    string;	// Worker id.
	begin: number;	// Begin days.
	work:  number;	// Work days.
}

interface T_TaskMemo
{
	id:  string;	// Task memo id.
	wid: string;	// Worker id.
	memo: string;	// memo.
}

interface T_Task
{
	id:      string;	// Task id.
	name:    string;	// Task name.
	begin:   string;	// Begin date.
	end:     string;	// End date.
	archive: boolean;	// Archive = true.
	memo:    T_TaskMemo[];
}

interface T_TaskData
{
	worker: T_Worker[];
	task: T_Task[];
}
